import React, {useState} from 'react';
import './SubmitButton.css';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


const SubmitButton = (props) => {
    const [showModal, setShowModal] = useState(false);

    const handleClose = () => setShowModal(false);
    const handleShow = () => setShowModal(true);

    function getRecipe(recipeUrl) {
        (async () => {
            const browser = await puppeteer.launch();
            const page = await browser.newPage();
            await page.goto('https://www.allrecipes.com/recipe/46653/taco-seasoning-i/');
            const recipeTitle = await page.$eval('.headline-wrapper > h1', el => el.textContent.trim());
            const ingredients = await page.$$eval('.ingredients-section__fieldset li', el => el.map(e => e.textContent.trim()));
            const instructions = await page.$eval('.section-body > .paragraph', el => el.textContent.trim());
            console.log(recipeTitle);
            console.log('Ingredients:');
            console.log(ingredients);
            console.log('Instructions:');
            console.log(instructions);
            await browser.close();
        })();
    }

    return (
        <div>
            <button className="submit-button" onClick={handleShow}>Give Me The Recipe</button>
            <Modal
                show={showModal}
                onHide={handleClose}
                centered
                fullscreen={true}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Here is your recipe:</Modal.Title>
                </Modal.Header>
                <Modal.Body><h2>{getRecipe(props.recipeUrl)}</h2></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Print Recipe
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default SubmitButton;
