import React, {useState} from 'react';
import './SubmitButton.css';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import * as cheerio from 'cheerio';



const SubmitButton = (props) => {
    const [showModal, setShowModal] = useState(false);

    const handleClose = () => setShowModal(false);
    const handleShow = () => setShowModal(true);

    function getRecipe(recipeUrl) {
        axios.get(`${recipeUrl}`)
            .then(response => {
                const $ = cheerio.load(response.data);
                const recipeTitle = $('.headline-wrapper > h1').text().trim();
                const ingredients = [];
                $('.ingredients-section__fieldset li').each((i, el) => {
                    ingredients.push($(el).text().trim());
                });
                const instructions = $('.section-body > .paragraph').text().trim();
                console.log(recipeTitle);
                console.log('Ingredients:');
                console.log(ingredients);
                console.log('Instructions:');
                console.log(instructions);
            })
            .catch(error => {
                console.log(error);
            });
    }

    return (
        <div>
            <button className="submit-button" onClick={handleShow}>Give Me The Recipe</button>
            <Modal
                show={showModal}
                onHide={handleClose}
                centered
                fullscreen={true}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Here is your recipe:</Modal.Title>
                </Modal.Header>
                <Modal.Body><h2>{getRecipe(props.recipeUrl)}</h2></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Print Recipe
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default SubmitButton;
